package Java_Learning;

public class S92_Do_While_Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 1;

		do 
		{
			System.out.println(i);
			i++;
		} 
		
		while (i > 10);
		{
			
		}
		System.out.println("after while loop");
	}
	// Do While - want to execute the code at least at once irrespective of what ever the condition is

}
